import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";


export const ContactsPage = () => {
  const [contacts, setContacts] = useState([]);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [isDuplicate, setIsDuplicate] = useState(false);


 

  const handleSubmit = (e) => {
    e.preventDefault();

    const isDuplicate = contacts.find(contact => contact.name === name);
    
    if (!isDuplicate) {
        setContacts(prev => [...prev, {name, phone, email}]);
        setName("");
        setPhone("");
        setEmail("");
       }
  };
    
  useEffect(() => { 
      if (isDuplicate) {
        setIsDuplicate(true)
        alert("Contact already exists");
      }
     },[isDuplicate]);

     const handlenameChange = (e) => { 
        setName(e.target.value);
      
     }

     const handlePhoneChange = (e) => {  
        setPhone(e.target.value);
     }

      const handleEmailChange = (e) => { 
        setEmail(e.target.value);
      }



  return (
    <div>
      <section>
        <h2> Add Contact</h2> 
        <ContactForm name={name} setName={handlenameChange} phone={phone} setPhone={handlePhoneChange} email={email} setEmail={handleEmailChange} handleSubmit={handleSubmit} />
      </section>
      <hr />
      <section>
        <h2> Contacts </h2>
        <TileList contacts={contacts} />
      </section>
    </div>
  );
};
